import React, { useState, useEffect } from 'react'
import { ReactSortable } from 'react-sortablejs'

export const Dragable = props => {
  const { itemData, setItemData } = props
  const [loading, setLoading] = useState(false)

  const findLastActive = () => {
    let i = 0
    while (itemData[i].active) i++
    return i
  }

  const activate = i => {
    let firstInactiveIdx = findLastActive()
    const act = itemData.slice(0, firstInactiveIdx)

    const inact = [
      ...itemData.slice(firstInactiveIdx, i),
      ...itemData.slice(i + 1, itemData.length)
    ]

    const updated = itemData[i]
    updated.active = true

    const newArray = [...act, updated, ...inact]

    setItemData(newArray)
  }

  const deactivate = i => {
    const newArray = [
      ...itemData.slice(0, i),
      ...itemData.slice(i + 1, itemData.length),
      itemData[i]
    ]

    newArray[newArray.length - 1].active = false

    setItemData(newArray)
  }

  const toggle = i => {
    itemData[i].active ? deactivate(i) : activate(i)
  }

  useEffect(() => {
    setLoading(!loading)
  }, [itemData])

  return (
    <div id='fields' className=''>

      {/* if itemData is loading, show loading text */}
      {itemData.length !== 0 ? (
        <></>
      ) : (
        <div className='flex justify-center'>
          <p className='mt-16 text-3xl animate-dots'>Loading</p>
        </div>
      )}

      <ReactSortable
        className='flex flex-col flex-wrap items-center'
        list={itemData}
        setList={setItemData}
      >
        {itemData.map((item, i) => {
          let activeClass
          if (item.active) {
            activeClass = ' active'
          } else {
            activeClass = ' inactive'
          }
          return (
            <div
              className={'flex m-3 items-center w-1/4 ' + activeClass}
              key={i}
            >
              <p className='ml-5'>{item.title}</p>
              <button
                className='ml-auto w-14 h-14 text-center text-3xl border-0'
                onClick={() => toggle(i)}
              >
                Ã—
              </button>
            </div>
          )
        })}
      </ReactSortable>
    </div>
  )
}
