import React from 'react';
import { ReactSortable } from 'react-sortablejs';

const Dragable = (props) => {
  const { itemData, setItemData, calculateTitle } = props;

  const activate = (i) => {
    const updatedItemData = itemData.map((item, index) => {
      if (index === i) {
        return { ...item, active: true };
      } else if (item.active) {
        return { ...item, active: false };
      }
      return item;
    });

    setItemData(updatedItemData);
  };

  const toggle = (i) => {
    const updatedItemData = itemData.map((item, index) => {
      if (index === i) {
        return { ...item, active: !item.active };
      }
      return item;
    });

    setItemData(updatedItemData);
  };

  return (
    <div id='fields' className=''>
      <ReactSortable
        className='flex flex-col flex-wrap items-center'
        list={itemData}
        setList={setItemData}
      >
        {itemData.map((item, i) => (
          <div
            className={`flex m-3 items-center w-1/4 ${item.active ? 'active' : 'inactive'}`}
            key={i}
          >
            <p className='ml-5'>{calculateTitle(item)}</p>
            <button
              className='ml-auto w-14 h-14 text-center text-3xl border-0'
              onClick={() => toggle(i)}
            >
              {item.active ? '✓' : '×'}
            </button>
          </div>
        ))}
      </ReactSortable>
    </div>
  );
};

export { Dragable };
