import './App.css';
import { useEffect, useState } from 'react';

import { create, getAll, deletePerson, updatePerson } from './services/people';


function App() {
  const [age, setAge] = useState(0);
  const [name, setName] = useState('');
  const [people, setPeople] = useState([]);

  useEffect(() => {
    getAll().then((response) => {
      setPeople(response.people);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    create(name, age).then((response) =>
      setPeople([...people, response.person])
    );
  };

  const handleDelete = (id) => {
    deletePerson(id).then(() =>
      setPeople(people.filter((person) => person.id !== id))
    );
  };

  const handleUpdate = (id) => {
    updatePerson(id, name, age).then((response) =>
      setPeople(
        people.map((person) => (person.id === id) ? response.person : person)
      )
    );
  };

  return (
    <div className="App">
      <h1 className="App-header">Add People</h1>
      <form className="Form" onSubmit={handleSubmit}>
        <input
          className="Form-input"
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="Form-input"
          type="number"
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <button className="Form-button" type="submit">Add</button>
      </form>
      <ul className="People-list">
        {people.map((person) => (
          <li key={person.id} className="Person-item">
            <span className="Person-info">
              {person.name} is {person.age} years old
            </span>
            <button className="Button-delete" onClick={() => handleDelete(person.id)}>Delete</button>
            <button className="Button-update" onClick={() => handleUpdate(person.id)}>Update</button>
          </li>
        ))}
      </ul>
    </div>
  );

}

export default App;
