import './App.css';
import { useEffect, useState } from 'react';

import { create, getAll, deletePerson, updatePerson } from './services/people';


function App() {
  const [age, setAge] = useState(0);
  const [name, setName] = useState('');
  const [people, setPeople] = useState([]);

  useEffect(() => {
    getAll().then((response) => {
      setPeople(response.people);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    create(name, age).then((response) => {
      setPeople([...people, response.person]);
    });
  };

  const handleDelete = (id) => {
    deletePerson(id).then(() => {
      const newPeople = people.filter((person) => person.id !== id);
      setPeople(newPeople);
    });
  };

  const handleUpdate = (id) => {
    updatePerson(id, name, age).then((response) => {
      console.log('response', response)
      const newPeople = people.map((person) => {
        if (person.id === id) {
          return response.person;
        } else {
          return person;
        }
      });
      setPeople(newPeople);
    });
  };

  return (
    <div className="App">
      <h1>Add People</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <button type="submit">Add</button>
      </form>
      <ul>
        {people.map((person) => (
          <li key={person.id}>
            {person.name} is {person.age} years old
            <button onClick={() => handleDelete(person.id)}>Delete</button>
            <button onClick={() => handleUpdate(person.id)}>Update</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
